import React from 'react'
import Layout from '../components/layout'
import Router, { withRouter } from 'next/router'
import { parseTime } from '../utils'
import Head from 'next/head'
import { fetchArtclsDetail } from '../api'

class Contact extends React.Component {
  static async getInitialProps({ req }) {
    const result = await fetchArtclsDetail({ _id: '5d4d61008ee6b62c8892a62e' })
    const contact = result.data
    return { contact }
  }
  componentDidMount() {
    //创建和初始化地图函数：
    function initMap(){
      createMap();//创建地图
      setMapEvent();//设置地图事件
      addMapControl();//向地图添加控件
      addMarker();//向地图中添加marker
    }
  
    //创建地图函数：
    function createMap(){
      var map = new BMap.Map("dituContent");//在百度地图容器中创建一个地图
      var point = new BMap.Point(115.7787010852, 28.1482020957);//定义一个中心点坐标
      map.centerAndZoom(point,18);//设定地图的中心点和坐标并将地图显示在地图容器中
      window.map = map;//将map变量存储在全局
    }
  
    //地图事件设置函数：
    function setMapEvent(){
      map.enableDragging();//启用地图拖拽事件，默认启用(可不写)
      map.enableScrollWheelZoom();//启用地图滚轮放大缩小
      map.enableDoubleClickZoom();//启用鼠标双击放大，默认启用(可不写)
      map.enableKeyboard();//启用键盘上下左右键移动地图
    }
  
    //地图控件添加函数：
    function addMapControl(){
            //向地图中添加缩放控件
      var ctrl_nav = new BMap.NavigationControl({anchor:BMAP_ANCHOR_TOP_LEFT,type:BMAP_NAVIGATION_CONTROL_LARGE});
      map.addControl(ctrl_nav);
            //向地图中添加缩略图控件
      var ctrl_ove = new BMap.OverviewMapControl({anchor:BMAP_ANCHOR_BOTTOM_RIGHT,isOpen:1});
      map.addControl(ctrl_ove);
            //向地图中添加比例尺控件
      var ctrl_sca = new BMap.ScaleControl({anchor:BMAP_ANCHOR_BOTTOM_LEFT});
      map.addControl(ctrl_sca);
    }
    // 120.215671,30.212136
    //标注点数组 115.7787010852,28.1482020957
    //http://api.map.baidu.com/lbsapi/getpoint/index.html
    var markerArr = [
      {
        title:"公司地址",
        content:"地址: 江西省丰城市紫云南大道1136号<br />总机: 0795-6288863<br />邮编: 331000",
        point:"115.7787010852, 28.1482020957",
        isOpen:0,
        icon:{w:30,h:30,l:0,t:0,x:6,lb:5}
      }
     ];
    //创建marker
    function addMarker(){
      for(var i=0;i<markerArr.length;i++){
        var json = markerArr[i];
        var p0 = json.point.split(",")[0];
        var p1 = json.point.split(",")[1];
        var point = new BMap.Point(p0,p1);
      var iconImg = createIcon(json.icon);
      var marker = new BMap.Marker(point,{icon:iconImg});
      var iw = createInfoWindow(i);
      var label = new BMap.Label(json.title,{"offset":new BMap.Size(json.icon.lb-json.icon.x+10,-20)});
      marker.setLabel(label);
      map.addOverlay(marker);
      label.setStyle({
        borderColor:"#808080",
        color:"#333",
        cursor:"pointer"
      });
  
      (function(){
        var index = i;
        var _iw = createInfoWindow(i);
        var _marker = marker;
  
        //hsguojz
        _marker.openInfoWindow(_iw);
        //hsguojz end
  
        _marker.addEventListener("click",function(){
            this.openInfoWindow(_iw);
          });
          _iw.addEventListener("open",function(){
            _marker.getLabel().hide();
          })
          _iw.addEventListener("close",function(){
            //_marker.getLabel().show();
          })
        label.addEventListener("click",function(){
            _marker.openInfoWindow(_iw);
          })
        if(!!json.isOpen){
          label.hide();
          _marker.openInfoWindow(_iw);
        }
      })()
      }
    }
    //创建InfoWindow
    function createInfoWindow(i){
      var json = markerArr[i];
      var iw = new BMap.InfoWindow("<b class='iw_poi_title' title='" + json.title + "'>" + json.title + "</b><div class='iw_poi_content'>"+json.content+"</div>");
      return iw;
    }
    //创建一个Icon
    function createIcon(json){
      var icon = new BMap.Icon("./static/images/address.png", new BMap.Size(json.w,json.h),{imageOffset: new BMap.Size(-json.l,-json.t),infoWindowOffset:new BMap.Size(json.lb+5,1),offset:new BMap.Size(json.x,json.h)})
      return icon;
    }
    initMap();//创建和初始化地图
  }
  createMarkup = (html) => {
    return { __html: html }
  }
  render() {
    const { contact } = this.props
    return (
      <Layout>
        <Head>
          <script type="text/javascript" src="http://api.map.baidu.com/api?key=&amp;v=1.1&amp;services=true"></script>
          <script type="text/javascript" src="http://api.map.baidu.com/getscript?v=1.1&amp;ak=&amp;services=true&amp;t=20130716024058"></script>
          <link rel="stylesheet" type="text/css" href="http://api.map.baidu.com/res/11/bmap.css" />
        </Head>
        <div className="snd-banner snd-contact">
          <div className="content-container">
            <img src="/static/images/contactbanner.jpg" />
          </div>
        </div>

        <div className="content-box snd-news">
          <div className="content-container">
            <div className="backtolist">
              <span><a onClick={() => { Router.back() }}>返回</a></span>
            </div>
            <div className="newsdetail">
              <div id="dituContent" style={{height: '440px', overflow: 'hidden'}}></div>
              <div className="detailcontent" dangerouslySetInnerHTML={this.createMarkup(contact.content)}></div>
            </div>
          </div>
        </div>
      </Layout> 
    )
  }  
}
export default withRouter(Contact)
