import React from 'react'
import Layout from '../components/layout'
import Router, { withRouter } from 'next/router'
import { parseTime } from '../utils'
import { fetchArtclsDetail } from '../api'

class About extends React.Component {
  static async getInitialProps({ req }) {
    const result = await fetchArtclsDetail({ _id: '5d4d60788ee6b62c8892a62a' })
    const aboutUs = result.data
    return { aboutUs }
  }
  createMarkup = (html) => {
    return { __html: html }
  }
  render() {
    const { aboutUs } = this.props
    return (
      <Layout>
        <div class="snd-banner product">
          <div class="content-container">
            <img src="/static/images/newsbanner.png" />
            <h1>公司简介</h1>
            <p>江西安邦工程质量检测有限公司要从事工程质量检测、建筑材料实验及检测技术咨询服务。</p>
          </div>
        </div>

        <div class="content-box snd-news">
          <div class="content-container">
            <div class="backtolist">
              <span><a href={Router.back()}>返回</a></span>
            </div>
            <div class="newsdetail">
              <h2>公司简介</h2>
              <span>{parseTime(aboutUs.createDate)}</span>
              <div class="detailcontent" dangerouslySetInnerHTML={this.createMarkup(aboutUs.content)}></div>
            </div>
          </div>
        </div>
      </Layout> 
    )
  }  
}
export default withRouter(About)
